import React, { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import "./App.scss";
import Layout from "./components/Layout";
import Home from "./pages/Home";
import AboutUs from "./pages/AboutUs";
import Devices from "./pages/Devices";
import ContactUs from "./pages/ContactUs";
import Projects from "./pages/Projects";
import templateImage from "./assets/images/template-image.jpg";

export const Context = React.createContext();
// App.js

function App() {
  const [projects, setProjects] = useState([
    {
      slug: "projekt-1",
      title: "Projekt 1",
      shortDescription:
        "Vaša firma se ističe u industriji kontrole pristupa u zgradama, pružajući inovativna rješenja za siguran i jednostavan ulazak. ",
      description: "Opis projekta 1",
      image: templateImage,
      address: "Adresa projekta 1",
      year: "2021",
      content: "Sadržaj projekta 1",
      type: "Ulazi u zgrade",
    },
    {
      slug: "projekt-2",
      title: "Projekt 2",
      shortDescription:
        "Vaša firma se ističe u industriji kontrole pristupa u zgradama, pružajući inovativna rješenja za siguran i jednostavan ulazak. ",
      description: "Opis projekta 1",
      image: templateImage,
      address: "Adresa projekta 1",
      year: "2021",
      content: "Sadržaj projekta 1",
      type: "Ulazi u zgrade",
    },
  ]);

  return (
    <Context.Provider value={{ projects, setProjects }}>
      <BrowserRouter>
        <Layout>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about-us" element={<AboutUs />} />
            <Route path="/devices" element={<Devices />} />
            <Route path="/contact-us" element={<ContactUs />} />
            <Route path="/projects" element={<Projects />} />
          </Routes>
        </Layout>
      </BrowserRouter>
    </Context.Provider>
  );
}

export default App;
