import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import heroSlideImageFirst from "../assets/images/hero-slide-1.jpg";

const Home = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCount((count) => (count + 1) % 4);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const heroSlides = [
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      description: (
        <>
          <p>Zgrade, Stanovi i ostalo</p>
          <p>
            Koristimo Sebury uređaje, omogućujući stanarima i ostalima da
            koriste privjeske, kartice ili PIN-ove za brz i pouzdan ulaz u
            objekte. Poboljšajte sigurnost i praktičnost s našim rješenjima za
            kontrolu pristupa.
          </p>
          <Link className="btn" to="/kontaktiraj-nas">
            Kontaktiraj nas
          </Link>
        </>
      ),
      image: heroSlideImageFirst,
    },
  ];

  return (
    <div className="page home">
      <div className="hero">{heroSlides[count].title}</div>
    </div>
  );
};

export default Home;
