    <>
      <BrowserRouter>
        <Routes>
          <Route element={<Layout />}>
            <Route path="/" element={<Home />} />
            <Route path="/o-nama" element={<AboutUs />} />
            <Route path="/uredaji" element={<Devices />} />
            <Route path="/kontaktiraj-nas" element={<ContactUs />} />
