import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import heroSlideImageFirst from "../assets/images/hero-slide-1.jpg";
import "../styles/home.scss";

const Home = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCount((count) => (count + 1) % 4);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const heroSlides = [
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
      description: (
        <>
          <p>Zgrade, Stanovi i ostalo</p>
          <p>
            Koristimo Sebury uređaje, omogućujući stanarima i ostalima da
            koriste privjeske, kartice ili PIN-ove za brz i pouzdan ulaz u
            objekte. Poboljšajte sigurnost i praktičnost s našim rješenjima za
            kontrolu pristupa.
          </p>
        </>
      ),
      actions: (
        <>
          <Link className="btn" to="/kontaktiraj-nas">
            naši uredaji
          </Link>
          <Link to="/kontaktiraj-nas">Saznjte više</Link>
        </>
      ),
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
      description: (
        <>
          <p>Zgrade, Stanovi i ostalo</p>
          <p>
            Koristimo Sebury uređaje, omogućujući stanarima i ostalima da
            koriste privjeske, kartice ili PIN-ove za brz i pouzdan ulaz u
            objekte. Poboljšajte sigurnost i praktičnost s našim rješenjima za
            kontrolu pristupa.
          </p>
        </>
      ),
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      image: heroSlideImageFirst,
      description: (
        <>
          <p>Zgrade, Stanovi i ostalo</p>
          <p>
            Koristimo Sebury uređaje, omogućujući stanarima i ostalima da
            koriste privjeske, kartice ili PIN-ove za brz i pouzdan ulaz u
            objekte. Poboljšajte sigurnost i praktičnost s našim rješenjima za
            kontrolu pristupa.
          </p>
        </>
      ),
    },
    {
      title: "KONTROLA PRISTUPA",
      subtitle: "KVALITETA I SIGURNOST",
      description: (
        <>
          <p>Zgrade, Stanovi i ostalo</p>
          <p>
            Koristimo Sebury uređaje, omogućujući stanarima i ostalima da
            koriste privjeske, kartice ili PIN-ove za brz i pouzdan ulaz u
            objekte. Poboljšajte sigurnost i praktičnost s našim rješenjima za
            kontrolu pristupa.
          </p>
        </>
      ),
      image: heroSlideImageFirst,
    },
  ];

  return (
    <div className="page home">
      <div className="hero">
        {heroSlides.map((slide, index) => (
          <div
            key={index}
            className={`slide ${index === count ? "active" : ""}`}
          >
            <div className="slide__image">
              <img src={slide.image} alt="" />
            </div>

            <div className="slide__content">
              <h2>{slide.subtitle}</h2>
              <h1>{slide.title}</h1>
              <div className="slide__description">{slide.description}</div>
              <div className="slide__actions">{slide.actions}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
